import streamlit as slimport functionsfrom functions import *todos = functions.load_todos()def new_todo():    todo = sl.session_state["new_todo"] + "\n"    todos.append(todo)    functions.save_todo(todos)sl.title("My To-Do App")sl.subheader("By: Darkotainment")sl.write("To-Do list")for index,todo in enumerate(todos):    checkbox = sl.checkbox(todo, key=todo)    if checkbox:        todos.pop(index)        functions.save_todo(todos)        del sl.session_state[todo]        sl.experimental_rerun()sl.text_input(label="Enter new To-Do",placeholder="Enter new To-Do",              key="new_todo", on_change=new_todo)